package Java;

public class LargestAmongThreeNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int x=900;
		int y=50;
		int z=100;
		
		if(x>y&&x>z)
		{
			System.out.println("x is highest");
		}
		else if(y>z)
		{
			System.out.println("Y is highest");
		}
		else
			System.out.println("Z is highest");

	}
}
